// Chanify script module for nodejs test.
// Support client version: 1.4.1
let args = {};

process.argv.forEach((arg) => {
	if (arg.startsWith('--')) {
		const items = arg.substring(2).split('=', 2); 
		if (items.length > 0) {
			args[items[0]] = (items.length > 1 ? items[1] : '');
		}
	}
});

module.exports = {
	args: args,
	pasteboard: 'Pasteboard',
	alert: (msg, cb) => {
		console.log(msg);
		if (cb) cb();
	},
	routeTo: url => {
		console.warn("route to:", url);
		return true;
	}
};
